          "hymn_1_lat": 5616.0,
          "hymn_2_lat": 0.0,
          "hymn_3_lat": 0.0,
          "ant1_lat": "Christum líbere confíteor, Christum ardénter sítio, cum Christo iúgiter esse cúpio.^LÖ",
